import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface PuzzleState {
  board: number[][];
  solution: number[][][];
  currentStep: number;
  isLoading: boolean;
  moves: number;
  timeElapsed: number;
}

const PRESET_PUZZLES = [
  {
    name: "Easy",
    board: [[1, 2, 3], [4, 0, 6], [7, 5, 8]]
  },
  {
    name: "Medium",
    board: [[1, 2, 3], [7, 4, 6], [5, 8, 0]]
  },
  {
    name: "Hard",
    board: [[7, 2, 4], [5, 0, 6], [8, 3, 1]]
  }
];

const App: React.FC = () => {
  const [size] = useState<number>(3);
  const [state, setState] = useState<PuzzleState>({
    board: Array(size).fill(0).map((_, i) => 
      Array(size).fill(0).map((_, j) => (i * size + j + 1) % (size * size))
    ),
    solution: [],
    currentStep: 0,
    isLoading: false,
    moves: 0,
    timeElapsed: 0
  });
  const [timer, setTimer] = useState<number | null>(null);

  useEffect(() => {
    if (state.isLoading && !timer) {
      const interval = setInterval(() => {
        setState(prev => ({
          ...prev,
          timeElapsed: prev.timeElapsed + 1
        }));
      }, 1000);
      setTimer(interval);
    } else if (!state.isLoading && timer) {
      clearInterval(timer);
      setTimer(null);
    }
    return () => {
      if (timer) clearInterval(timer);
    };
  }, [state.isLoading, timer]);

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const loadPresetPuzzle = (preset: typeof PRESET_PUZZLES[0]) => {
    setState({
      board: preset.board,
      solution: [],
      currentStep: 0,
      isLoading: false,
      moves: 0,
      timeElapsed: 0
    });
  };

  const handleTileClick = (row: number, col: number) => {
    const newBoard = [...state.board.map(r => [...r])];
    const emptyPos = findEmptyPosition(newBoard);

    if (isAdjacent(row, col, emptyPos.row, emptyPos.col)) {
      newBoard[emptyPos.row][emptyPos.col] = newBoard[row][col];
      newBoard[row][col] = 0;
      setState({
        ...state,
        board: newBoard,
        moves: state.moves + 1
      });
    }
  };

  const findEmptyPosition = (board: number[][]) => {
    for (let i = 0; i < board.length; i++) {
      for (let j = 0; j < board[i].length; j++) {
        if (board[i][j] === 0) return { row: i, col: j };
      }
    }
    return { row: -1, col: -1 };
  };

  const isAdjacent = (row1: number, col1: number, row2: number, col2: number) => {
    return Math.abs(row1 - row2) + Math.abs(col1 - col2) === 1;
  };

  const shuffleBoard = () => {
    const moves = 100;
    let newBoard = [...state.board.map(r => [...r])];

    for (let i = 0; i < moves; i++) {
      const emptyPos = findEmptyPosition(newBoard);
      const possibleMoves = [
        { row: emptyPos.row - 1, col: emptyPos.col },
        { row: emptyPos.row + 1, col: emptyPos.col },
        { row: emptyPos.row, col: emptyPos.col - 1 },
        { row: emptyPos.row, col: emptyPos.col + 1 }
      ].filter(move => 
        move.row >= 0 && move.row < size && 
        move.col >= 0 && move.col < size
      );

      const randomMove = possibleMoves[Math.floor(Math.random() * possibleMoves.length)];
      newBoard[emptyPos.row][emptyPos.col] = newBoard[randomMove.row][randomMove.col];
      newBoard[randomMove.row][randomMove.col] = 0;
    }

    setState({
      ...state,
      board: newBoard,
      solution: [],
      currentStep: 0,
      moves: 0,
      timeElapsed: 0
    });
  };

  const solvePuzzle = async () => {
    setState({ ...state, isLoading: true });
    try {
      const response = await axios.post('http://localhost:8000/solve', {
        board: state.board
      });
      setState({
        ...state,
        solution: response.data.solution,
        isLoading: false,
        currentStep: 0
      });
    } catch (err) {
      setState({
        ...state,
        isLoading: false,
        solution: [],
        currentStep: 0
      });
    }
  };

  const nextStep = () => {
    if (state.currentStep < state.solution.length - 1) {
      setState({
        ...state,
        board: state.solution[state.currentStep + 1],
        currentStep: state.currentStep + 1
      });
    }
  };

  const prevStep = () => {
    if (state.currentStep > 0) {
      setState({
        ...state,
        board: state.solution[state.currentStep - 1],
        currentStep: state.currentStep - 1
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-blue-500 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl"></div>
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="text-center pb-8">
                <h1 className="text-3xl font-bold text-gray-900">N-Puzzle Solver</h1>
                <p className="mt-2 text-gray-600">Using A* Algorithm</p>
                <div className="mt-4 flex justify-center space-x-2">
                  {PRESET_PUZZLES.map((preset) => (
                    <button
                      key={preset.name}
                      onClick={() => loadPresetPuzzle(preset)}
                      className="px-3 py-1 text-sm bg-indigo-100 text-indigo-700 rounded hover:bg-indigo-200 transition-colors duration-200"
                    >
                      {preset.name}
                    </button>
                  ))}
                </div>
              </div>
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <div className="flex justify-between items-center mb-4 text-sm font-medium">
                  <span className="text-blue-600">Moves: {state.moves}</span>
                  <span className="text-green-600">Time: {formatTime(state.timeElapsed)}</span>
                </div>
                <div className="grid grid-cols-3 gap-2 mb-4">
                  {state.board.map((row, i) => (
                    <div key={i} className="flex justify-center">
                      {row.map((tile, j) => (
                        <button
                          key={`${i}-${j}`}
                          className={`w-16 h-16 m-1 text-xl font-bold rounded ${
                            tile === 0 
                              ? 'bg-gray-200' 
                              : 'bg-blue-500 text-white hover:bg-blue-600'
                          }`}
                          onClick={() => handleTileClick(i, j)}
                        >
                          {tile === 0 ? '' : tile}
                        </button>
                      ))}
                    </div>
                  ))}
                </div>

                <div className="flex justify-center space-x-4 mb-4">
                  <button
                    className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                    onClick={shuffleBoard}
                    disabled={state.isLoading}
                  >
                    Shuffle
                  </button>
                  <button
                    className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 transition-colors duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                    onClick={solvePuzzle}
                    disabled={state.isLoading}
                  >
                    {state.isLoading ? 'Solving...' : 'Solve'}
                  </button>
                </div>

                {state.solution.length > 0 && (
                  <div className="flex flex-col items-center space-y-4">
                    <div className="text-sm font-medium text-gray-600">
                      Solution: Step {state.currentStep + 1} of {state.solution.length}
                    </div>
                    <div className="flex justify-center space-x-4">
                      <button
                        className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 transition-colors duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                        onClick={prevStep}
                        disabled={state.currentStep === 0}
                      >
                        Previous
                      </button>
                      <button
                        className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 transition-colors duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                        onClick={nextStep}
                        disabled={state.currentStep === state.solution.length - 1}
                      >
                        Next
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
